import { createInterface } from "node:readline/promises";

import { openWs } from "./utils/ws.js";
import { SINGLE_TRADE_PROMPT_CHALLENGE_EVENT } from "../src/root/single_trade/single_trade.constants.js";

const baseURL = "http://localhost:8072";

const rl = createInterface({
  input: process.stdin,
  output: process.stdout,
});

const ws = await openWs(baseURL.replace("http", "ws"));

ws.on("message", async (message) => {
  const data = JSON.parse(message.toString());
  console.dir(data.data, { depth: null });
  if (data.event === SINGLE_TRADE_PROMPT_CHALLENGE_EVENT) {
    const code = await rl.question(`Enter code for User ${data.data.userId}: `);
    const response = await fetch(
      `${baseURL}/single-trades/${data.data.singleTradeId}/${data.data.userId}/solve-challenge?code=${code}`
    );
    console.log(`Response: ${response.status} ${response.statusText}`);
  }
});

